import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';


let products = [];
if(products === null){
    products = [];
}

localStorage.setItem("products", JSON.stringify(products));

class ReactBasic extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            name:"",
            price:"",
            productItems: [],
        }
    }




handleNameChange = (event)=>{
        let nameState = event.target.value;
        this.setState({name: nameState});
        //console.log(nameState)
}


handlePriceChange = (event)=>{
    let priceState = event.target.value;
    this.setState({price: priceState});
    //console.log(nameState)
}

handleSubmit = (event)=>{
        event.preventDefault();

}

handleClick = ()=>{
    console.log("clicked")
}

componentWillMount(){
    console.log(this.state.name);
}


    render(){
        return(
            <div className="container-fluid">
                <h1>CRUD APP</h1>

                <form onSubmit={this.handleSubmit}>
                    <input type="text" placeholder="Name" onChange={this.handleNameChange}></input> {`  `}
                    <input type="text" placeholder="price" onChange={this.handlePriceChange}></input>
                    <button className="btn-primary" onClick={this.handleClick}>Add item</button>
                </form>

                <p>{this.state.name} {` | `} {this.state.price}</p>
            </div>
        )
    }
}

export default ReactBasic;
