import React from 'react';



const ProductItem = (props) =>{
    const {name, price , onDelete} = props;


   
        
   
    return (<div>
        <span>{name}</span> 
        <span>{price}</span><br/>
        <button onClick={onDelete}>Delete</button>
    </div>
    );

};




const products = [
    {
        name:"ipad",
        price:"650"
    },

    {
        name:"iphone",
        price:"1000"
    },
    {
        name:"samsung",
        price:"450"
    }
];
localStorage.setItem("products", JSON.stringify(products));

class ReactBasic extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            productsShow: [],
        }
    };

    componentWillMount(){
        const products1 = this.getProducts(); 
        this.setState({productsShow: products1});
    };
    

    getProducts(){
        return JSON.parse(localStorage.getItem("products"));
    }

    handleDelete =(name)=>{
        const products1 = this.getProducts(); 
        const filtProd = products1.filter(item =>{
            return item.name !== name;
        })

        console.log(filtProd);
    }


    render(){
        
        return (
            <div>
                <h1>My App</h1>
                {this.state.productsShow.map(item =>{
                    return <ProductItem key = {item.name} {...item} onDelete = {() =>this.handleDelete(item.name)}/>
                        
             })}

             
            </div>
        )
    }
}

export default ReactBasic;
