import React from 'react';



const ShowItem = (props) =>{
    const {name, price , onDelete} = props;
    return (<div>
                <span>{name}</span> {` | `}
                <span>{price}</span><br/>
                <button onClick={onDelete}>Delete</button>
            </div>
    );

};







const products = [
 
];
products.push({name: "kingsley", surname: "onyelo"});
localStorage.setItem("products", JSON.stringify(products));

class ReactBasic extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            productsShow: JSON.parse(localStorage.getItem("products")),
            isEdit: false,
            name: "",
            price:""
        }
    };

    componentWillMount(){
        /* const products1 = this.getProducts(); 
        this.setState({productsShow: products1}) */;
        //console.log();
        //console.log(JSON.parse(localStorage.getItem("products")))
        
    };
    

    getProducts(){
        return this.state.productsShow;
    }

     handleAdd = (name, price)=>{
        const products = this.getProducts();
        products.push({name, price});
       // console.log(products);
        this.setState({productsShow:products});
        
    } 

    handleSub = (event)=>{
        event.preventDefault();
        //console.log(this.inputName.value, this.inputPrice.value);
    }

    handleEdit = ()=>{

        this.setState({isEdit: true});

    }

    handleDelete =(name)=>{
        const products1 = this.getProducts(); 
        const filtProd = products1.filter(item =>{
            return item.name !== name;
        })
        this.setState({productsShow: filtProd});
        //console.log(filtProd);
    }


    render(){
        
        return (
            <div>
                <h1>My App</h1>
                
                <form onSubmit = {this.handleSub}>
                    <input type="text" ref={inputName => this.inputName = inputName} placeholder="Name" value={this.state.name}/>
                    <input type="text" ref={inputPrice => this.inputPrice = inputPrice} placeholder="Price" value={this.state.price}/>
               
                    <button onClick={()=>this.handleAdd(this.inputName.value, this.inputPrice.value)}>Add item</button>
                    </form>
                    

                
                     {this.state.productsShow.map(item =>{
                                return <ShowItem key = {item.name} {...item} 
                                
                                onDelete = {() =>this.handleDelete(item.name)} handleSub={this.onSubmitMe}
                                
                                />
                                    
                         })}
                

             
            </div>
        )
    }
}

export default ReactBasic;
