import React from 'react';
import './style.css';
import 'bootstrap/dist/css/bootstrap.css'
import axios from 'axios';
import {Link} from 'react-router-dom';



export default class Sign extends React.Component{
    constructor(props){
        super(props);


        this.state = {
            department:"",
            name:"",
            email:"",
            password:"",
          

        };
    }


  


    onChange = (e)=>{
        this.setState({[e.target.name]: e.target.value});
    }
  

    

   

async submitForm(){
    
   
        try {
            const res = await axios.post("http://localhost:2004/teachers", this.state);
            console.log(res.data);
            const token = res.data.data.token;
            console.log(token);
            /* localStorage.setItem("blog-token", token);
            this.props.history.push("/profile"); */
            
        } catch (error) {
            console.log("there was an error", error.response);
            
        }
         
     }

    
    render(){
        return (
            <div>
                <nav className="navBar">
                                <Link to="/"><div className="spn"><h3 className="logo">TimeOff App</h3></div></Link>
                                    <div className="signUp">
                                    <Link to="/login"><input type="button" className="btn btn1" value="Login"></input></Link>
                                    <Link to="/sign"><input type="button" className="btn btn2" value="Sign Up"></input></Link>
                                    </div>
                                </nav>
        <div className="signDiv1">
            

            <div className="signCon2">
            
               <form>
                <label className="lbl">SIGN UP</label>
                <input type="text" placeholder="Department" className="form-control" onChange={this.onChange} value={this.state.department}></input>
               
                <input type="text" placeholder="Name" className="form-control" onChange= {this.onChange}></input>
                
                <input type="email" placeholder="Email Address" className="form-control" onChange= {this.onChange}></input>
           
                <input type="number" placeholder="Age" className="form-control" onChange= {this.onChange}></input>
              
                <input type="password" placeholder="Password" className="form-control" onChange={this.onChange} ></input>
                <br/>
                <input type="password" placeholder="Confirm Password" className="form-control" onChange={this.onChange} ></input>
            
          
                <br/>
                <select name="Country" className="fname form-control">
                    <option value="Canada" >Canada</option>
                    <option value="USA">USA</option>
                    <option value="Dubai">Dubai</option>
                    <option value="Ghana">Ghana</option>
                </select><br/>
                <input type="submit" value="Submit" className="fname1" onClick={this.submitForm}/>>
            </form>
            
                
                
            </div>
            

            
        </div>
        </div>
        
        );
    }
}
