import React from 'react';


 const Incomp = (props)=>{
        return <h1>{props.heading}</h1>
}

const products = [
    {
        name:"ipad",
        price:"650"
    },

    {
        name:"iphone",
        price:"1000"
    },
    {
        name:"samsung",
        price:"450"
    }
];
localStorage.setItem("products", JSON.stringify(products));

class ReactBasic extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            productsShow: [],
        }
    };

    componentWillMount(){
      this.getProducts(); 
    };
    

    getProducts(){
        const products1 = JSON.parse(localStorage.getItem("products"));

        this.setState({productsShow: products1});
        
    }

    render(){
        
        return (
            <div>
                <h1>My App</h1>
                {this.state.productsShow.map(item =>{
                    return (<div key={item.name}><span>{item.name}</span> | 
                    <span>{item.price}</span><br/>
                    <button>Delete</button> </div>);
                    
                })}

                <Incomp heading= "hello world"/>
            </div>
        )
    }
}

export default ReactBasic;
