import React from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './note.css';



const noteItems = [{noteTile: 'title1', noteContent: 'content1'},
{noteTile: 'title2', noteContent: 'content2'},
{noteTile: 'title3', noteContent: 'content3'}]



//import noteIcon from './img/noteIcon.svg';
class Notes extends React.Component{

    constructor(props){
        super(props);

        this.handleChange = this.handleChange.bind(this);
        this.handleClick = this.handleClick.bind(this);

        this.state = {
            noteTitle : "",
            noteContent : "",
            

        }
    }

    


    handleChange(event){
        let noteTitle = event.target.value;
        this.setState({noteTitle: noteTitle});
    }

    handleClick(event){
        event.preventDefault();
        console.log(this.state);
   
    }

    render(){

            
        return(
            <div>
                <div className="container2">
                <nav className="navbar navbar-custom">
                <p className="navbar-brand customName" >Fancy Notes</p>
            <button className="btn btn-outline-success find" type="submit" data-toggle="modal" data-target="#exampleModal">New Note</button>
            </nav>
            {
                noteItems.map(item => {
                    return  <div className="cards">
                    <div className="cardHolder">
                       <div className="cardName">
                        <h1 style={{color:"white"}}>Note Title</h1>
                       </div>
    
                        
                    </div>
                </div>
                })
            }

            </div>


<div className="modal fade" id="exampleModal"  role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog" role="document">
    <div className="modal-content">
      <div className="modal-header">
        <input type="text" className="modal-title" id="exampleModalLabel" placeholder="Note Title" onChange={this.handleChange}></input>
        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body">
      <textarea id="form7" class="md-textarea form-control" rows="3" placeholder="Content"></textarea>
      </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" className="btn btn-primary" onClick={this.handleClick}>Save</button>
      </div>
    </div>
  </div>
</div>






            </div>
        )
    }
}

export default Notes;
