import React from 'react';



class ReactBasic extends React.Component{


    componentWillMount(){
        console.log("show first");
    }

    componentDidMount(){
        console.log("show second");
    }
submitUs = (event) =>{
    event.preventDefault();
    console.log(this.input.value);
}

    render(){
        const titles = ["one list", "two list", "three list"];
        return (
            <div>
                <h1>{titles.map(item => {
                    return (<li key={item}>item</li>)
                })}</h1>
                <form onSubmit={this.submitUs}>
                {true ? console.log("yes") : <input type="text"  onChange={this.typeUs} ref={pnt => this.input = pnt}/>
}

                </form>
            </div>
        )
    }
}

export default ReactBasic;
