import React from 'react';

const NewComp = (props) =>{
    return <h1 onClick = {props.onclick}>Hello world</h1>
};

const ProductItem = (props) =>{
    const {name, price} = props;
    return (<div>
        <span>{name}</span>{`|`} 
        <span>{price}</span>{`|`}
        <button>Delete</button>
    </div>
    );

};




const products = [
    {
        name:"ipad",
        price:"650"
    },

    {
        name:"iphone",
        price:"1000"
    },
    {
        name:"samsung",
        price:"450"
    }
];
localStorage.setItem("products", JSON.stringify(products));

class ReactBasic extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            productsShow: [],
        }
    };

    componentWillMount(){
      this.getProducts(); 
    };
    

    getProducts(){
        const products1 = JSON.parse(localStorage.getItem("products"));

        this.setState({productsShow: products1});
        
    }

    
handleclick = ()=>{
    console.log("have been clicked");
}

    render(){
        
        return (
            <div>
                <h1>My App</h1>
                {this.state.productsShow.map(item =>{
                    return (
                        <div>
                            <ProductItem name = {item.name} />
                        </div>
                    );
                    
                })}

             <NewComp onclick={this.handleclick}/>
            </div>
        )
    }
}

export default ReactBasic;
